INSERT INTO `osc_home_reviews_items` (`id`, `image`, `comment`, `author`, `signature`, `ru_comment`, `ru_author`, `ru_signature`, `en_comment`, `en_author`, `en_signature`, `fr_comment`, `fr_author`, `fr_signature`, `block`, `order_id`, `dateCreate`, `dateModify`, `adminMod`) VALUES
(1, NULL, 'אני ממש מבסוט מהאפשרות לשמור את הדברים בקופסת אחסון, שגם מביאים אלי, גם מובילים למחסן וגם מחזירים עד הבית מתי שאני צריך. זה חזק! חבל לבזבז זמן בחיפוש פתרונות אחרים.', ' דניאל כהן ', '', '\"Это очень удобно использовать такие коробки для хранения, которые мне еще и привезут, и вернут, когда мне будет нужно. Это круто. Не тратьте время на поиски другой компании.\"           ', 'Даниель Коэн', '', '\"I am thrilled with having the option of storage boxes delivered to me when I need them and returned. It\'s just all too good and amazing. Don\'t waste time looking at other storage companies.\"                                                          ', 'Daniel Cohen', '', 'C’est très  pratique d’utiliser les box pour le stockage en plus ils sont livrés et retournés quand j’en ai besoi. C’est cool. Ne perdez pas votre temps pour chercher ailleurs.', 'Daniel Cohen', '', 0, 1, '2017-05-04 22:56:46', '2017-06-29 16:03:49', 0),
(2, NULL, 'הדברים שלא היה להם מקום - סופסוף מצאו את הפינה שלהם איזה הקלה!!', 'ברברה שטיין', '', '\"Мои вещи, у которых не было \"дома\", наконец,  нашли свое место.\"', 'Барбара Штейн', '', '\"Things in the house that didn\'y have a \"home\" before, now have a place to live.\"                                                                                                                 ', 'Barbara Shtein', '', 'Mes affaires « SDF «  ont trouvé leur maison.', 'Barbara Stein', '', 0, 2, '2017-05-04 22:58:35', '2017-06-29 16:04:27', 0),
(3, NULL, 'לגדל שני ילדים קטנים בדירה  קטנה זה לא קל. פתרון האחסון החכם של ישראסטוראג\' התברר כממש מושלם עבורינו. כל הדברים שישבו לנו על הראש ולא באמת נחוצים על בסיס יומיומי מאורגנים עכשיו במקום אחד ולא מפוזרים בכל הבית. זה מדהים, פתאם הבית מרגיש יותר גדול, ויש יותר מקום לכולם. הפסקנו לדבר על מעבר מחוץ לעיר.', ' יונתן', '', '\"Растить двух маленьких детей в Хадере стало намного проще с IsraStorage. Теперь наши вещи не разбросаны по всей квартире. У нас стало намного больше пространства. И нам больше не нужно думать о переезде за город.\"\r\n\r\n\r\n', 'Йонатан', '', '“Raising two young children in Hadera is now much easier because of IsraStorage. All our “stuff” is no longer spread throughout the condo. We live better and have more room. Seriously…now we don’t need to move to the suburbs!” ', 'Jonathan', '', 'C’est devenu plus facile pour une famille avec deux enfants à Hadera avec IsraStorage. Maintenant  tout est bien rangé chez nous. Nous avons beaucoup plus d’espace. Nous ne pensons plus au déménagement.', 'Jonathan', '', 0, 3, '2017-05-04 22:59:24', '2017-06-29 16:04:57', 0),
(4, NULL, 'אני אוהב את זה כי אפשר הכל לעשות אונליין.\r\n\r\n\r\n\r\n', 'אביגיל', '', '\"Обожаю этот сервис, потому что всё можно сделать онлайн.\"', 'Абигайл', '', '\"I love that you can manage everything online.\"', 'Avigail', '', 'J’adore ce service : on peut tout faire en-ligne.', 'Abigale', '', 0, 4, '2017-05-07 01:21:03', '2017-06-29 16:05:21', 0),
