<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 0;		$password = $_POST['new-password'];		$cardUpd = array(					'name'			=> $_POST['name'],					'locale'			=> $_POST['locale'],					'fname'			=> $_POST['fname'],					'login'			=> $_POST['login'],										'phone'			=> $_POST['phone'],					'second_phone'	=> $_POST['second_phone'],					'block'			=> 1,					'male'			=> $_POST['male'][0],					'delivery_address'		=> $_POST['delivery_address'],					'comment'		=> $_POST['comment'],										'dateCreate'	=> date("Y-m-d H:i:s", time()),					'dateModify'	=> date("Y-m-d H:i:s", time())					);						if(true || !empty($cardUpd['login']) && filter_var($cardUpd['login'],FILTER_VALIDATE_EMAIL,true))	{						// $query = "SELECT id FROM [pre]alt_users WHERE `login`='".$cardUpd['login']."' AND `login`!='' AND `login`!=' ' LIMIT 1";		// $findUser = $ah->rs($query);				// if(!$findUser)		// {				$pass = ($password!="" ? $password : substr(md5(time()+rand(1000,9999),8)));				$cardUpd['pass'] = md5($pass);				// File upload 				$filename = "avatar";				if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)		{			$file_path = "../../../../split/files/users/";						$file_update = $ah->mtvc_add_files_file(array(					'path'			=>$file_path,					'name'			=>5,					'pre'			=>"alt_user_",					'size'			=>10,					'rule'			=>0,					'max_w'			=>2500,					'max_h'			=>2500,					'files'			=>$filename,					'resize_path'	=>$file_path."crop/",					'resize_w'		=>150,					'resize_h'		=>150				  ));			if($file_update)			{				$cardUpd[$filename] = $file_update;			}		}				// Create main table item				$query = "INSERT INTO [pre]$appTable ";				$fieldsStr = " ( ";				$valuesStr = " ( ";				$cntUpd = 0;		foreach($cardUpd as $field => $itemUpd)		{			$cntUpd++;						$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");						$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");		}				$fieldsStr .= " ) ";				$valuesStr .= " ) ";				$query .= $fieldsStr." VALUES ".$valuesStr;					$data['block'] = $cardUpd['block'];				$data['query'] = $query;					$ah->rs($query);				$item_id = mysql_insert_id();						$data['item_id'] = $item_id;				$data['status'] = "success";		$data['message'] = "Success user save";										// }else				// {				// 	$data['status'] = "failed";				// 	$data['message'] = "Клиент с таким логином уже существует.";				// }					}else	{		$data['status'] = "failed";		$data['message'] = "Не корректный Email";	}	