<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'code'			=> $_POST['code'],					'value'			=> (int)$_POST['value'],										'expires'			=> date("Y-m-d H:i:s",strtotime($_POST['expires'])),										'user_for'			=> (int)$_POST['user_for'],					'reusable'			=> (int)$_POST['reusable'][0],					'created'			=> date("Y-m-d H:i:s"),					'modified'			=> date("Y-m-d H:i:s")					);		// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);		if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}		$data['status'] = 'success';	$data['message'] = "Новый VIRTUAL BOX успешно создан. ID = ".$item_id;	