<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'user_id'			=> (int)$_POST['user_id'],					'order_id'			=> (int)$_POST['order_id'],					'status'			=> (int)$_POST['status'],					'pay_method_id'		=> (int)$_POST['pay_method_id'],					'total'				=> (int)$_POST['total'],					'comment'			=> $_POST['comment'],										'dateCreate'		=> date("Y-m-d H:i:s"),					'dateModify'		=> date("Y-m-d H:i:s")					);		// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);		if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}		$data['status'] = 'success';	$data['message'] = "PAYMENT CREATED. ID = ".$item_id;	