<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = (isset($_POST['item_id']) ? $_POST['item_id'] : 0);		$cardUpd = array(					'page'			=> $_POST['page'],					'text'			=> $_POST['text'],					'en_text'		=> $_POST['en_text'],					'ru_text'		=> $_POST['ru_text'],					'fr_text'		=> $_POST['fr_text']					);		// Create main table item		$query = "INSERT INTO [pre]$appTable ";		$fieldsStr = " ( ";		$valuesStr = " ( ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;				$fieldsStr .= ($cntUpd==1 ? "`$field`" : ", `$field`");				$valuesStr .= ($cntUpd==1 ? "'$itemUpd'" : ", '$itemUpd'");	}		$fieldsStr .= " ) ";		$valuesStr .= " ) ";		$query .= $fieldsStr." VALUES ".$valuesStr;			$item_id = $ah->rs($query,0,1,1);		//$item_id = mysql_insert_id();		if($item_id)	{		$data['item_id'] = $item_id;	}else	{		$data['item_id'] = 0;	}		$data['message'] = "NEW TRANSLATE ID = $item_id";	