<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(					'name'			=> $_POST['name'],					'locale'			=> $_POST['locale'],					'fname'			=> $_POST['fname'],					'login'			=> $_POST['login'],					'activity'		=> $_POST['activity'][0],										'phone'			=> $_POST['phone'],					'second_phone'	=> $_POST['second_phone'],					'block'			=> 1,					'male'			=> $_POST['male'][0],					'delivery_address'		=> $_POST['delivery_address'],					'comment'		=> $_POST['comment'],										'dateModify'	=> date("Y-m-d H:i:s", time())					);						// File upload 		//echo "<pre>"; print_r($cardUpd); echo "</pre>"; exit();		$filename = "avatar";		$file_update = false;	if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../split/files/users/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"alt_user_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>"0", //$file_path."crop/",				'resize_w'		=>0, //150,				'resize_h'		=>0, //150,				'resize_path_2'	=>"0", 				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;			$data['message'] = "file $filename = ".$file_update;			//echo json_encode($data); exit();						$curr_filename = $_POST['curr_filename'];						unlink($file_path.$curr_filename);		}	}		$filename = "data";		$file_update = false;	if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_path = "../../../../split/files/users/";				$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"alt_user_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename,				'resize_path'	=>"0", //$file_path."crop/",				'resize_w'		=>0, //150,				'resize_h'		=>0, //150,				'resize_path_2'	=>"0", 				'resize_w_2'	=>0,				'resize_h_2'	=>0			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;			$curr_filename = $_POST['curr_data'];						unlink($file_path.$curr_filename);		}	}	// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";			$data['block'] = $cardUpd['block'];		$data['query'] = $query;			$ah->rs($query);		$data['message'] = "Success user save. ";