<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;	$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					$lang_prefix.'title' => $_POST['title'],										$lang_prefix.'form_name' 	=> $_POST['form_name'],					$lang_prefix.'form_email' 	=> $_POST['form_email'],					$lang_prefix.'form_phone' 	=> $_POST['form_phone'],					$lang_prefix.'form_message'	=> $_POST['form_message'],										$lang_prefix.'details' => $_POST['details'],										'send_to' 		=> $_POST['send_to'],					'map_w' 		=> (float)$_POST['map_w'],					'map_l' 		=> (float)$_POST['map_l'],										'map_icon1_w' 	=> (float)$_POST['map_icon1_w'],					'map_icon1_l' 	=> (float)$_POST['map_icon1_l'],										'map_icon2_w' 	=> (float)$_POST['map_icon2_w'],					'map_icon2_l' 	=> (float)$_POST['map_icon2_l'],										'map_icon3_w' 	=> (float)$_POST['map_icon3_w'],					'map_icon3_l' 	=> (float)$_POST['map_icon3_l'],										'map_zoom' 		=> (int)$_POST['map_zoom'],					'map_scroll' 	=> (int)$_POST['map_scroll'][0],										'dateModify'	=> date("Y-m-d H:i:s", time())					);		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);			$data['message'] = "Контактные данные успешно сохранены."; // <pre>$query</pre>	