<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = 1;		$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					$lang_prefix.'title'	=> $_POST['title'],										$lang_prefix.'caption1'	=> $_POST['caption1'],					$lang_prefix.'details1'	=> $_POST['details1'],					$lang_prefix.'text1'	=> $_POST['text1'],										$lang_prefix.'caption2'	=> $_POST['caption2'],					$lang_prefix.'details2'	=> $_POST['details2'],					$lang_prefix.'text2'	=> $_POST['text2'],										'dateModify'	=> date("Y-m-d H:i:s", time())					);						// File upload 	$file_path = "../../../../split/files/content/";		$filename = "filename1";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"price_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_'.$filename];						unlink($file_path.$curr_filename);		}	}		$filename = "filename2";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"price_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_'.$filename];						unlink($file_path.$curr_filename);		}	}		// Update main table		$query = "UPDATE `$appTable` SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query,0,1);		$data['message'] = "Home Prices saved successfully.";	