<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$lpx = $_POST['lpx'];	$lang_prefix = ($lpx ? $lpx."_" : ""); // (Israel - empty, ru, en, fr)		$cardUpd = array(					$lang_prefix.'comment'		=> $_POST['comment'],					$lang_prefix.'author'		=> $_POST['author'],					$lang_prefix.'signature'	=> $_POST['signature'],					//'image'			=> $_POST['image'],					'block'			=> $_POST['block'][0],					'order_id'		=> $_POST['order_id'],										'dateModify'	=> date("Y-m-d H:i:s", time())					);	$file_path = "../../../../split/files/content/";		$filename = "image";		if(isset($_FILES[$filename]) && $_FILES[$filename]['size'] > 0)	{		$file_update = $ah->mtvc_add_files_file(array(				'path'			=>$file_path,				'name'			=>5,				'pre'			=>"service_",				'size'			=>10,				'rule'			=>0,				'max_w'			=>2500,				'max_h'			=>2500,				'files'			=>$filename			  ));		if($file_update)		{			$cardUpd[$filename] = $file_update;						$curr_filename = $_POST['curr_'.$filename];						unlink($file_path.$curr_filename);		}	}							// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);		$data['message'] = "Home Services Item #$item_id успешно сохранен.";	