<?php 	//********************	//** WEB MIRACLE TECHNOLOGIES	//********************		// get post		$appTable = $_POST['appTable'];		$item_id = $_POST['item_id'];		$cardUpd = array(										'user_id'			=> (int)$_POST['user_id'],					'order_id'			=> (int)$_POST['order_id'],					'status'			=> (int)$_POST['status'],					'pay_method_id'		=> (int)$_POST['pay_method_id'],					'total'				=> (int)$_POST['total'],					'comment'			=> $_POST['comment'],										'dateModify'		=> date("Y-m-d H:i:s")					);		// Update main table		$query = "UPDATE [pre]$appTable SET ";		$cntUpd = 0;	foreach($cardUpd as $field => $itemUpd)	{		$cntUpd++;		$query .= ($cntUpd==1 ? "`$field`='$itemUpd'" : ", `$field`='$itemUpd'");	}		$query .= " WHERE `id`=$item_id LIMIT 1";				$ah->rs($query);		$data['q'] = $query;	$data['status'] = 'success';	$data['message'] = "PAYMENT SAVED.";	