<?php 	//********************	//** WEB INSPECTOR	//********************		require_once "../../../require.base.php";		require_once "../../library/AjaxHelp.php";		$ah = new ajaxHelp($dbh);		$order_id 	= (int)$_POST['order_id'];		$action = (isset($_POST['action']) ? $_POST['action'] : "open");	$r = array('status'=>'success','message'=>'BOX ADDED','articel'=>0);			$date = date("Y-m-d H:i:s");			switch($action)	{		case 'add':						$q = "SELECT * FROM [pre]o_boxes_kinds WHERE `id`=1 LIMIT 1";			$box_kind = $ah->dbh->q($q,1);						if($box_kind)			{				$price = $box_kind['price'];							$q = "INSERT INTO [pre]o_orders_boxes 					(`order_id`,`box_id`,`box_kind_id`,`price`,`comment`,`filename`,`created`)					VALUES					('$order_id','0','1','$price','','','$date') 					";				$ah->dbh->q($q,0,1);			}			break;				case 'add_pallet':						$q = "SELECT * FROM [pre]o_boxes_kinds WHERE `id`=3 LIMIT 1";			$box_kind = $ah->dbh->q($q,1);						if($box_kind)			{				$price = $box_kind['price'];							$q = "INSERT INTO [pre]o_orders_boxes 					(`order_id`,`box_id`,`box_kind_id`,`price`,`comment`,`filename`,`created`)					VALUES					('$order_id','0','3','$price','','','$date') 					";				$ah->dbh->q($q,0,1);			}			break;				case 'add_clothe':						$q = "SELECT * FROM [pre]o_boxes_kinds WHERE `id`=2 LIMIT 1";			$box_kind = $ah->dbh->q($q,1);						if($box_kind)			{				$price = $box_kind['price'];							$q = "INSERT INTO [pre]o_orders_boxes 					(`order_id`,`box_id`,`box_kind_id`,`price`,`comment`,`filename`,`created`)					VALUES					('$order_id','0','2','$price','','','$date') 					";				$ah->dbh->q($q,0,1);			}			break;		case 'rent':						$q = "SELECT * FROM [pre]o_boxes_kinds WHERE `id`=1 LIMIT 1";			$box_kind = $ah->dbh->q($q,1);						if($box_kind)			{				$price = $box_kind['rent_price'];							$q = "INSERT INTO [pre]o_orders_boxes 					(`order_id`,`box_id`,`box_kind_id`,`price`,`comment`,`filename`,`created`)					VALUES					('$order_id','0','1','$price','','','$date') 					";				$ah->dbh->q($q,0,1);			}				break;		default:break;	}		echo json_encode($r); exit();