<?php 	//********************	//** WEB INSPECTOR	//********************		require_once "../../../require.base.php";		require_once "../../library/AjaxHelp.php";		$ah = new ajaxHelp($dbh);		$ref_id 		= (int)$_POST['ref_id'];		$action = (isset($_POST['action']) ? $_POST['action'] : "open");	$r = array('status'=>'success','message'=>'BOX удален','articel'=>0);			switch($action)	{		case 'delete':						$q = "SELECT * FROM [pre]o_orders_boxes WHERE `id`='$ref_id' LIMIT 1";			$box = $ah->dbh->q($q,1);						$box_id = (isset($box['box_id']) ? (int)$box['box_id'] : 0);			$q = "SELECT * FROM [pre]o_orders_boxes_ref WHERE `ref_id`='$ref_id' LIMIT 1";			$items = $ah->dbh->q($q,1);						foreach($items as $item)			{				if($item)				{					$filepath = "../../../../".$item['filename'];					chmod($filepath,0777);					unlink($filepath);				}			}							$q = "DELETE FROM [pre]o_orders_boxes WHERE `id`='$ref_id' LIMIT 1";			$ah->dbh->q($q,0,1);						$q = "DELETE FROM [pre]o_orders_boxes_ref WHERE `ref_id`='$ref_id' LIMIT 100";			$ah->dbh->q($q,0,1);			if($box_id){				$q = "UPDATE [pre]o_boxes SET `order_id`='0' WHERE `id`='$box_id' LIMIT 1";				$ah->dbh->q($q,0,1);			}						echo json_encode($r); exit();			break;		default:break;	}