<?php 	//********************	//** WEB INSPECTOR	//********************		require_once "../../../require.base.php";		require_once "../../library/AjaxHelp.php";		$ah = new ajaxHelp($dbh);		$id 		= (int)$_POST['id'];		$action = (isset($_POST['action']) ? $_POST['action'] : "open");	$r = array('status'=>'success','message'=>'Изображение удалено','articel'=>0);			switch($action)	{		case 'delete':						$q = "SELECT * FROM [pre]o_orders_boxes_ref WHERE `id`='$id' LIMIT 1";			$item = $ah->dbh->q($q,1);						if($item)			{				$filepath = "../../../../".$item['filename'];				chmod($filepath,0777);				unlink($filepath);			}						$q = "DELETE FROM [pre]o_orders_boxes_ref WHERE `id`='$id' LIMIT 1";			$ah->dbh->q($q,0,1);						echo json_encode($r); exit();			break;		default:break;	}